import { FcDeleteDatabase } from "react-icons/fc"
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  }

  return (
    <div>

      <div className=" flex w-80">

        <div>
          <img src={item.image} alt=" " className=" max-h-[500px]" />
        </div>
        <div>
          <h1 className="text-gray-700 font-semibold text-lg  text-left mt-1">{item.title}</h1>
          <h1 className=" text-gray-400 font-normal text-[20px] text-left">{item.description}</h1>
          <div className=" flex justify-between">
            <p className="text-green-600 font-semibold"> ${item.price}</p>
            <div
              onClick={removeFromCart}>
              <FcDeleteDatabase className="text-2xl" />
            </div>
          </div>

        </div>


      </div>

    </div>
  );
};

export default CartItem;
